f = open(file, 'r')
hostname = None dir = None port = None adminport = None sslport = None jmxport = None rootDn = None rootPwd = None baseDn = None changelogport = None suffixList = [] changelogList = [] serverId = 1
fileLines = f.readlines() for line in fileLines:
if line.startswith('#'): continue elif line.find('Hostname') != -1: hostname = line[line.find('Hostname') + 9:].strip() elif line.find('Directory') !=-1: dir = line[line.find('Directory') + 10:].strip() elif line.find('Port') != -1: port = line[line.find('Port') + 5:].strip() elif line.find('Adminport') != -1: adminport = line[line.find('Adminport') + 10:].strip() elif line.find('Sslport') != -1: sslport = line[line.find('Sslport') + 8:].strip() elif line.find('Jmxport') != -1: jmxport = line[line.find('Jmxport') + 8:].strip() elif line.find('RootDn') != -1: rootDn = line[line.find('RootDn') + 7:].strip() elif line.find('RootPwd') != -1: rootPwd = line[line.find('RootPwd') + 8:].strip() elif line.find('BaseDn') != -1: baseDn = line[line.find('BaseDn') + 7:].strip() elif line.find('Changelogport') != -1: changelogport = line[line.find('Changelogport') + 14:].strip() elif line.find('SynchronizedSuffix') != -1: suffixList.append( line[line.find('SynchronizedSuffix') + 19:].strip() ) elif line.find('ChangelogServer') != -1: changelogList.append( line[line.find('ChangelogServer') + 16:].strip() ) elif (line.isspace()) or (len(line) == 0): server = Server(hostname, dir, port, adminport, sslport, jmxport, rootDn, rootPwd, baseDn)
if changelogport != None: changelogServer = ChangelogServer(changelogport, serverId) for item in changelogList: srv = item[:item.find(':')] prt = item[item.find(':') + 1:] changelogServer.addChangelogServer(srv, prt) server.addChangelogServer(changelogServer)
for suffix in suffixList: synchroSuffix = SynchronizedSuffix(suffix, serverId) for item in changelogList: srv = item[:item.find(':')] prt = item[item.find(':') + 1:] synchroSuffix.addChangelogServer(srv, prt) server.addSynchronizedSuffix(synchroSuffix)
_topologyServerList.append(server)
hostname = None dir = None port = None adminport = None sslport = None jmxport = None rootDn = None rootPwd = None baseDn = None changelogport = None suffixList = [] changelogList = [] serverId += 1
if hostname != None: server = Server(hostname, dir, port, adminport, sslport, jmxport, rootDn, rootPwd, baseDn)
